function V = flattening(I,pixel,img_num)
P=zeros(pixel*pixel,1);%生成拼接图片所需的转存矩阵 
for n=1:img_num
    PP=eval(['im2double(I{',num2str(n),'})']);
    PP = PP./max(max(PP));
    for i=1:pixel
        P((i-1)*pixel+1:i*pixel,n)=PP(:,i);
    end
end                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
V=P;
end